mapper.insert:
select activity_seq.nextval from dual
insert into activity( activity_no, activity_title, activity_content, activity_thumb, activity_price, activity_area
) values( #{activity_no}, #{activity_title}, #{activity_content}, #{activity_thumb}, #{activity_price}, #{activity_area}
)

mapper.select:
select * from (
	select rownum as rn, a.*
		from (
			select * from activity
			order by activity_no desc
			) a		
)
where rn between #{start} and #{end}
